package hashtable.RepeatedDNASequences_187;

import org.junit.jupiter.api.Test;

import java.util.Arrays;
import java.util.Collections;

import static org.junit.jupiter.api.Assertions.assertEquals;

public class SolutionTest {
    @Test
    public void test() {
        Solution s = new Solution();
        assertEquals(Arrays.asList("AAAAACCCCC", "CCCCCAAAAA"),
                s.findRepeatedDnaSequences("AAAAACCCCCAAAAACCCCCCAAAAAGGGTTT"));
        assertEquals(Collections.singletonList("AAAAAAAAAA"),
                s.findRepeatedDnaSequences("AAAAAAAAAAAAA"));
        assertEquals(Collections.singletonList("GTTCTCACTA"),
                s.findRepeatedDnaSequences(""));
    }
}