package string.CheckIfAStringContainsAllBinaryCodesOfSizeK_1461;

import org.junit.Test;

import static org.junit.Assert.*;

public class SolutionTest {
    @Test
    public void test() {
        Solution s = new Solution();
        assertTrue(s.hasAllCodes("00110110", 2));
        assertTrue(s.hasAllCodes("00110", 2));
        assertTrue(s.hasAllCodes("0110", 1));
        assertFalse(s.hasAllCodes("0110", 2));
        assertFalse(s.hasAllCodes("0000000001011100", 4));
        assertFalse(s.hasAllCodes("00000000010011101", 4));
        assertTrue(s.hasAllCodes("00110110", 2));
        assertFalse(s.hasAllCodes("", 20));
    }
}
