package string.LongestPalindromicSubstring_5;

import org.junit.Test;

import static org.junit.Assert.assertEquals;

public class SolutionTest {
    @Test
    public void test() {
        Solution s = new Solution();
        assertEquals("bab", s.longestPalindrome("babad"));
        assertEquals("bb", s.longestPalindrome("cbbd"));
        assertEquals("bababab", s.longestPalindrome("babababfassaf"));
        assertEquals("bb", s.longestPalindrome("bb"));
        assertEquals("b", s.longestPalindrome("b"));
        assertEquals("a", s.longestPalindrome("ab"));
        assertEquals("",
                s.longestPalindrome(""));
    }
}
